cells.data:
.twenty_five {
  //adjust in line with margins
  width: 23.5%;
  //adjust to fit needs then set media queries below
  min-width: 150px;
  margin-bottom: 1%;
  box-sizing: initial;
  display: inline-block;
  vertical-align: top;
  img {
    max-width: 100%;
  }
  &:nth-child(4n+1) {
    margin-right: 1%;
  }
  &:nth-child(4n+2) {
    margin-left: 1%;
    margin-right: 1%;
  }
  &:nth-child(4n+3) {
    margin-left: 1%;
    margin-right: 1%;
  }
  &:nth-child(4n+4) {
    margin-left: 1%;
  }
  @media only screen and (max-width: 664px) {
    width: 48%;
    margin-bottom: 4%;

    &:nth-child(4n+1) {
      margin-right: 1%;
      margin-left: 0%;
    }
    &:nth-child(4n+2) {
      margin-right: 0%;
      margin-left: 1%;
    }
    &:nth-child(4n+3) {
      margin-right: 1%;
      margin-left: 0%;
    }
    &:nth-child(4n+4) {
      margin-right: 0%;
      margin-left: 1%;
    }
  }
  @media only screen and (max-width: 400px) {
    width: 100%;
    &:nth-child(4n+1) {
      margin-right: 0%;
      margin-left: 0%;
    }
    &:nth-child(4n+2) {
      margin-right: 0%;
      margin-left: 0%;
    }
    &:nth-child(4n+3) {
      margin-right: 0%;
      margin-left: 0%;
    }
    &:nth-child(4n+4) {
      margin-right: 0%;
      margin-left: 0%;
    }
  }
}
