//ONLY FOR BRAVE INTERPID DEVELOPERS
//GRAND PAGE WRAPPER - NO FIXED WITH OVER 100PX ANYWHERE INSIDE
.gcontainer {
  width: 100%;
  padding-right: 2%;
  padding-left: 2%;
  margin: 0 auto;
  max-width: 1300px;
  @media only screen and (max-width: 600px) {
    padding:0%;
  }
}
//SECTION WRAPPING, EACH NEW ROW SHOULD HAVE A NEW SECTION, OR IF RELATED OR SHARING A BACKGROUND REPLACE THE INNER WRAPPER
section {
  width: 100%;
  text-align: center;
  //INNER WRAPPERS ARE AWESOME< WRAP UP LITTLE BLOCK WITHIN 
  .inner_wrapper {
    display: inline-block;
    width: 100%;
    //BLOCK LEVEL ELEMENTS
    .block {
      display: inline-block;
      vertical-align: top;
      box-sizing: border-box;
      img {
        width: 100%;
      }
    }
  }
}
.block.hundred{
  width:100%
}
.block.eighty{
  width:80%
}

.block.fifty {
  width: 49%;
  min-width: 280px;
  margin-bottom: 1%;
  &:nth-child(2n+1) {
    margin-right: 1%;
    margin-left: 0%;
  }
  &:nth-child(2n+2) {
    margin-right: 0%;
    margin-left: 1%;
  }
  @media only screen and (max-width: 600px) {
    width: 100%;
    &:nth-child(2n+1) {
      margin-left: 0%;
      margin-right: 0%;
      margin-top: 0%;
      margin-bottom: 20px
    }
    &:nth-child(2n+2) {
      margin-left: 0%;
      margin-right: 0%;
      margin-top: 0%;
      margin-bottom: 20px
    }
  }
}

.block.thirty {
  width: 32%;
  min-width: 220px;
  margin-bottom: 1%;
  &:nth-child(3n+1) {
    margin-right: 0%;
  }
  &:nth-child(3n+2) {
    margin-left: 2%;
    margin-right: 2%;
  }
  &:nth-child(3n+3) {
    margin-left: 0%;
  }
  @media only screen and (max-width: 716px) {
    width: 49%;
    &:nth-child(2n+1) {
      margin-right: 2%;
      margin-left: 0%;
    }
    &:nth-child(2n+2) {
      margin-right: 0%;
      margin-left: 0%;
    }
    &:nth-child(3n+3){
      width:100%;
    }
  }
  @media only screen and (max-width: 500px) {
    width: 100%;
    &:nth-child(2n+1) {
      margin-left: 0%;
      margin-right: 0%;
      margin-top: 0%;
      margin-bottom: 20px
    }
    &:nth-child(2n+2) {
      margin-left: 0%;
      margin-right: 0%;
      margin-top: 0%;
      margin-bottom: 20px
    }
  }
}
.twenty_five {
  width: 23.5%;
  min-width: 150px;
  margin-bottom: 1%;
  img {
    max-width: 100%;
  }
  &:nth-child(4n+1) {
    margin-right: 1%;
  }
  &:nth-child(4n+2) {
    margin-left: 1%;
    margin-right: 1%;
  }
  &:nth-child(4n+3) {
    margin-left: 1%;
    margin-right: 1%;
  }
  &:nth-child(4n+4) {
    margin-left: 1%;
  }
  @media only screen and (max-width: 664px) {
    width: 48%;
    margin-bottom: 4%;

    &:nth-child(4n+1) {
      margin-right: 1%;
      margin-left: 0%;
    }
    &:nth-child(4n+2) {
      margin-right: 0%;
      margin-left: 1%;
    }
    &:nth-child(4n+3) {
      margin-right: 1%;
      margin-left: 0%;
    }
    &:nth-child(4n+4) {
      margin-right: 0%;
      margin-left: 1%;
    }
  }
  @media only screen and (max-width: 400px) {
    width: 100%;
    &:nth-child(4n+1) {
      margin-right: 0%;
      margin-left: 0%;
    }
    &:nth-child(4n+2) {
      margin-right: 0%;
      margin-left: 0%;
    }
    &:nth-child(4n+3) {
      margin-right: 0%;
      margin-left: 0%;
    }
    &:nth-child(4n+4) {
      margin-right: 0%;
      margin-left: 0%;
    }
  }
}
