cells.data:
.block.thirty {
  //adjust in line with margins
  width: 32%;
  //adjust to fit needs then set media queries below
  min-width: 220px;
  margin-bottom: 1%;
  box-sizing: initial;
  display: inline-block;
  vertical-align: top;
  text-align: center;
  img {
    max-width: 100%;
  }
  &:nth-child(3n+1) {
    margin-right: 0%;
  }
  &:nth-child(3n+2) {
    margin-left: 2%;
    margin-right: 2%;
  }
  &:nth-child(3n+3) {
    margin-left: 0%;
  }
  @media only screen and (max-width: 716px) {
    width: 49%;
    &:nth-child(2n+1) {
      margin-right: 2%;
      margin-left: 0%;
    }
    &:nth-child(2n+2) {
      margin-right: 0%;
      margin-left: 0%;
    }
    &:nth-child(3n+3){
      width:100%;
    }
  }
  @media only screen and (max-width: 500px) {
    width: 100%;
    &:nth-child(2n+1) {
      margin-left: 0%;
      margin-right: 0%;
      margin-top: 0%;
      margin-bottom: 20px
    }
    &:nth-child(2n+2) {
      margin-left: 0%;
      margin-right: 0%;
      margin-top: 0%;
      margin-bottom: 20px
    }
  }
}
