{% if data.relate_all_thirty|length > 0 %}
    <section id="thirty_relationship">
        <div class="inner_wrapper gcontainer">
              {% set relate_all = TimberPost(data.relate_all_thirty) %}
              {% for relation in relate_all %}
                  <div class="block thirty">
                      <a href="{{ relation.link }}">
                          <img src="{{ TimberImage(relation.thumbnail).src('thirty_twenty_block') }}">
                      </a>
                      <h3>{{ relation.title }}</h3>
                      <p>{{ relation.post_excerpt }}</p>
                  </div>
              {% endfor %}
        </div>
    </section>
{% endif %}
