cells.data:
.blocksix {
  padding-top: 5%;
  padding-bottom: 7%;
  .inner_wrapper {
    .blocksixes {
      width: 33.3%;
      min-width: 300px;
      height: auto;
      min-height: 300px;
      position: relative;
      display: inline-block;
      @media only screen and (max-width: 991px) {
        width: 50%;
      }
      @media only screen and (max-width: 630px) {
        width: 100%;
      }
      .image {
        position: absolute;
        height: 97%;
        width: 97%;
        background-size: cover;
        background-position: center top;
        margin: auto;
        top: 0;
        left: 0;
        bottom: 0;
        right: 0;
        .inner_block {
          position: absolute;
          width: 100%;
          height: 100%;
        }
        .hero_tag {
          position: absolute;
          width: 100%;
          height: 100%;
          padding: 5%;
          text-align: center;
          h3 {
            position: relative;
            top: 50%;
            text-transform: uppercase;
            transform: translateY(-50%);
            margin: 0px;
            color: white;
            letter-spacing: 4px;
            font-size: 36px;
          }
        }
        &:hover .inner_block {
          opacity: 0;
        }
      }
    }
  }
}
.flexible {
  .inner_wrapper {
    .blockflex {
      display: inline-block;
      vertical-align: top;
      text-align: justify;
      min-width: 250px;
      padding-top: 2%;
      padding-bottom: 5%;
      padding-left: 6px;
      padding-right: 6px;
      &.pad_left {
        padding-left: 1%;
        padding-right: 6px;
      }
      &.pad_right {
        padding-right: 5%;
        padding-left: 6px;
      }
      @media only screen and (max-width: 630px) {
        padding-left: 0px;
        padding-right: 0px;

        &.pad_left, &.pad_right {
          padding-left: 9px;
          padding-right: 9px;
        }
      }
      img{
        width:100%;
      }
      h3 {
        &:nth-child(1) {
          margin-top: 0px;
        }
      }
      .button_alt{
        width:auto;
        text-align: center;
        margin: 0px auto;
        padding-top: 2%;
        padding-bottom: 2%;
        padding-left: 3%;
        padding-right: 3%;
      }
    }
    .one {
      width: 100%;
      @media only screen and (max-width: 630px) {
        padding-left:0%;
        padding-right:0%;
      }
    }
    .two {
      width: 50%;
      @media only screen and (max-width: 630px) {
        width:100%;
        padding-bottom:1%;
      }
    }
    .three {
      width: 33.3%;
    }
    .four {
      width: 25%;
    }
    .five {
      width: 20%;
    }
    .seventy{
      width:70%;
      @media only screen and (max-width: 991px) {
        width:100%;
        padding-bottom:1%;
      }
    }
    .thirty{
      width:30%;
      @media only screen and (max-width: 991px) {
        width:100%;
        padding-bottom:1%;
      }
    }
  }
}
