//IF BUTTON SELECT
if ($field['checkbox_flex_cta_text'] == true) {
    //ON SITE LINK
    if ($field['button_flex_select'] == 'on-site') { ?>
        <a class="main_cta"
           href="<?php echo $field['page_link_flexible_text'] ?>"><?php echo $field['flex_text_button_text'] ?></a>
        <?php
    } //OFF SITE LINK
    elseif ($field['button_flex_select'] == 'off-site') { ?>
        <a class="main_cta" target="_blank"
           href="<?php echo $field['site_link_flexible_text'] ?>"><?php echo $field['flex_text_button_text'] ?></a>
        <?php
    }
}
?>
