#mobile_nav {
  display: none;
  @media only screen and (max-width:50em) {
    display: block;
    height: 40px;
    background-color: transparent;
    position: fixed;
    width: 100%;
    z-index:250;
    top:0px;
  }
  .nav {
    position: relative;
    width: auto;
    display: inline-block;
    border: none;
  }
  .menu-item {
    width: 80%;
    margin-left:auto;
    margin-right:auto;
    :after {
      content: '';
      display:block;
      border: 1px solid white ;
      width:10px;
      margin-left:auto;
      margin-right:auto;
      margin-bottom:5px;
      margin-top:5px;
    }

    a {
      color: white;
      font-weight: 700;
      font-size:20px;
      text-transform: uppercase;
      position: relative;
      text-decoration: none;
      margin: auto;
    }
  }
}
.btn-nav {
  position: fixed;
  top: 0px;
  right: 0px;
  border: none;
  -webkit-transition: all .5s ease;
  -moz-transition: all .5s ease;
  -ms-transition: all .5s ease;
  -o-transition: all .5s ease;
  transition: all .5s ease;
  cursor: pointer;
  z-index: 99999;
  background-color: transparent;
  padding-left: 20px;
  padding-right: 20px;
  padding-top: 13px;
  border-bottom-left-radius: 10px;
}

.btn-nav:focus {
  outline: 0;
}

.icon-bar {
  display: block;
  margin: 6px 0;
  width: 40px;
  height: 5px;
  /* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#2676bf+0,4d408f+100 */
  background: rgb(38,118,191); /* Old browsers */
  background: -moz-linear-gradient(-45deg,  rgba(38,118,191,1) 0%, rgba(77,64,143,1) 100%); /* FF3.6-15 */
  background: -webkit-linear-gradient(-45deg,  rgba(38,118,191,1) 0%,rgba(77,64,143,1) 100%); /* Chrome10-25,Safari5.1-6 */
  background: linear-gradient(135deg,  rgba(38,118,191,1) 0%,rgba(77,64,143,1) 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
  filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#2676bf', endColorstr='#4d408f',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
}

.btn-nav:hover .icon-bar {
  -webkit-transition: all 1s ease;
  -moz-transition: all 1s ease;
  -ms-transition: all 1s ease;
  -o-transition: all 1s ease;
  transition: all 1s ease;
  /* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#f0682e+0,f4ae32+100 */
  background: rgb(240,104,46); /* Old browsers */
  background: -moz-linear-gradient(-45deg,  rgba(240,104,46,1) 0%, rgba(244,174,50,1) 100%); /* FF3.6-15 */
  background: -webkit-linear-gradient(-45deg,  rgba(240,104,46,1) 0%,rgba(244,174,50,1) 100%); /* Chrome10-25,Safari5.1-6 */
  background: linear-gradient(135deg,  rgba(240,104,46,1) 0%,rgba(244,174,50,1) 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
  filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#f0682e', endColorstr='#f4ae32',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */

}

.nav-content {
  position: fixed;
  top: -100%;
  bottom: 0;
  left: 0;
  right: 0;
  /* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#2676bf+0,4d408f+100 */
  background: rgb(38,118,191); /* Old browsers */
  background: -moz-linear-gradient(-45deg,  rgba(38,118,191,1) 0%, rgba(77,64,143,1) 100%); /* FF3.6-15 */
  background: -webkit-linear-gradient(-45deg,  rgba(38,118,191,1) 0%,rgba(77,64,143,1) 100%); /* Chrome10-25,Safari5.1-6 */
  background: linear-gradient(135deg,  rgba(38,118,191,1) 0%,rgba(77,64,143,1) 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
  filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#2676bf', endColorstr='#4d408f',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
  display: block;
  height: 100%;
  z-index: 90;
}

.nav-list {
  list-style: none;
  padding: 0;
  position: relative;
  top: 5%;
}

.nav-item {
  margin: 40px auto;
  text-align: center;
}

.animated {
  display: block;
  margin: 0 auto;
}

.animated:hover .icon-bar,
.animated:focus .icon-bar{
  background-color: white;
}

.animated:focus {
  cursor: pointer;
  z-index: 9999;
}

.middle {
  margin: 0 auto;
}

.icon-bar {
  -webkit-transition: all .7s ease;
  -moz-transition: all .7s ease;
  -ms-transition: all .7s ease;
  -o-transition: all .7s ease;
  transition: all .7s ease;
  z-index: 999999;
}

.animated .icon-bar {
  z-index: 999999;
  background-color: orange;
}

.animated .top {
  -webkit-transform: translateY(10px) rotateZ(45deg);
  -moz-transform: translateY(10px) rotateZ(45deg);
  -ms-transform: translateY(10px) rotateZ(45deg);
  -o-transform: translateY(10px) rotateZ(45deg);
  transform: translateY(10px) rotateZ(45deg);
}

.animated .bottom {
  -webkit-transform: translateY(-10px) rotateZ(-45deg);
  -moz-transform: translateY(-10px) rotateZ(-45deg);
  -ms-transform: translateY(-10px) rotateZ(-45deg);
  -o-transform: translateY(-10px) rotateZ(-45deg);
  transform: translateY(-10px) rotateZ(-45deg);
}

.animated .middle {
  width: 0;
}

@keyframes showNav {
  from {
    top: -100%;
  }
  to {
    top: 0;
  }
}

@-webkit-keyframes showNav {
  from {
    top: -100%;
  }
  to {
    top: 0;
  }
}

@-moz-keyframes showNav {
  from {
    top: -100%;
  }
  to {
    top: 0;
  }
}

@-o-keyframes showNav {
  from {
    top: -100%;
  }
  to {
    top: 0;
  }
}

.showNav {
  -webkit-animation: showNav 1s ease forwards;
  -moz-animation: showNav 1s ease forwards;
  -o-animation: showNav 1s ease forwards;
  animation: showNav 1s ease forwards;
}

@keyframes hideNav {
  from {
    top: 0;
  }
  to {
    top: -100%;
  }
}

@-webkit-keyframes hideNav {
  from {
    top: 0;
  }
  to {
    top: -100%;
  }
}

@-moz-keyframes hideNav {
  from {
    top: 0;
  }
  to {
    top: -100%;
  }
}

@-o-keyframes hideNav {
  from {
    top: 0;
  }
  to {
    top: -100%;
  }
}

.hideNav {
  -webkit-animation: hideNav 1s ease forwards;
  -moz-animation: hideNav 1s ease forwards;
  -o-animation: hideNav 1s ease forwards;
  animation: hideNav 1s ease forwards;
}

.hidden {
  display: none;
}
